const bytes = ""

const hre = require("hardhat");

async function main() {
    console.log(
        hre.ethers.utils.defaultAbiCoder.decode(["uint256", "bytes"], bytes)
    );
}

// We recommend this pattern to be able to use async/await everywhere
// and properly handle errors.
main().catch((error) => {
    console.error(error);
    process.exitCode = 1;
});